            getFirstMatchingExtract(matches) {
                const matchingExtracts = matches.filter(m => m.key === 'extract');
                return matchingExtracts.length > 0 ? matchingExtracts[0]:null;
            },
            filterResults() {
                return this.result.filter(
                    r => this.filter.languageFacets[r.item.language].included
                    && this.filter.downloads.min <= r.item.nbDownloads
                    && r.item.nbDownloads <= this.max
                );
            },
            sortResults() {
                this.result.sort((r1, r2) => {
                    switch (this.sort.by) {
                        case 'nbDownloads':
                            if (isNaN(r1.item.nbDownloads)) {
                                return 1;
                            }
                            if (isNaN(r2.item.nbDownloads)) {
                                return -1;
                            }
                            return r2.item.nbDownloads - r1.item.nbDownloads;
                        case 'score':
                            return  r2.score - r1.score;
                        case 'author':
                        case 'title':
                        case 'language':
                            if (typeof (r1.item[this.sort.by]) === 'undefined') {
                                return 1;
                            }
                            if (typeof (r2.item[this.sort.by]) === 'undefined') {
                                return -1;
                            }
                            return r1.item[this.sort.by].localeCompare(r2.item[this.sort.by]);
                        default:
                            return 0;
                    }
                })
            },
            computeFacets() {
                this.filter.languageFacets = {};
                this.filter.downloads.min = -1;
                this.filter.downloads.max = 0;
                this.result.forEach((r) => {
                    this.feedLanguageFacets(r.item);
                    this.setNbDownloads(r.item);
                    this.feedDownloadsFilter(r.item.nbDownloads);
                });
            },
            feedLanguageFacets(item) {
                let languageFacet = this.filter.languageFacets[item.language]
                if (typeof(languageFacet) === 'undefined') {
                    languageFacet = {quantity: 1, included:true};
                    this.filter.languageFacets[item.language] = languageFacet;
                } else {
                    languageFacet.quantity++;
                }
            },
            feedDownloadsFilter(nbDownloads) {
                if (!isNaN(nbDownloads)) {
                    if (this.filter.downloads.min < 0 || nbDownloads < this.filter.downloads.min) {
                        this.filter.downloads.min = nbDownloads
                    }
                    if (this.filter.downloads.max < nbDownloads) {
                        this.filter.downloads.max = nbDownloads;
                    }
                }
            },
            setNbDownloads(item) {
                if (typeof(item.downloads) !== 'undefined') {
                    const index = item.downloads.indexOf(" downloads in the last 30 days.");
                    if (index !== -1) {
                        const nbDownloads = parseInt(item.downloads.slice(0, index));
                        if (!isNaN(nbDownloads)) {
                            item.nbDownloads = nbDownloads;
                        }
